commits.content:
.contenido-principal {
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
    background-color: #0F1A24;
    padding-top: 50px;
    flex: 1;
}
.contenedor {
    background-color: #282828;
    padding: 30px;
    border-radius: 10px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
    max-width: 400px;
    width: 100%;
}
.contenedor h2 {
    text-align: center;
}
.grupo-formulario {
    margin-bottom: 15px;
    text-align: left;
}
label {
    display: block;
    margin-bottom: 10px;
}
input[type="text"],
textarea,
input[type="date"],
select {
    width: 100%;
    padding: 10px;
    border: none;
    border-radius: 5px;
    box-sizing: border-box;
    margin-bottom: 15px;
}
textarea {
    height: 100px;
}
.botones {
    display: flex;
    justify-content: space-between;
}
.botones button {
    padding: 10px 20px;
    border: none;
    border-radius: 5px;
    cursor: pointer;
    font-size: 16px;
    transition: background-color 0.3s ease, transform 0.3s ease;
}
.botones .guardar {
    background-color: #3A76F0;
    color: #fff;
}
.botones .guardar:hover {
    background-color: #3458A1;
    transform: translateY(-3px);
}
.botones .cancelar {
    background-color: #F03A47;
    color: #fff;
}
.botones .cancelar:hover {
    background-color: #B52933;
    transform: translateY(-3px);
}
.botones button:active {
    transform: scale(0.98);
}
