+import { Component, OnInit } from '@angular/core';
+import { Discuciones } from '../../models/discuciones';
+import { Perfil } from '../../models/perfil';
+import { Respuestas } from '../../models/respuestas';
+import { ActivatedRoute } from '@angular/router';
+import { PerfilService } from '../../service/perfilService';
+import { DiscucionesService } from '../../service/discucionesService';
+import { RespuestasService } from '../../service/respuestasService';
+import { map } from 'rxjs';
+
+@Component({
+  selector: 'app-ver-mensajes',
+  standalone: true,
+  imports: [],
+  templateUrl: './ver-mensajes.component.html',
+  styleUrl: './ver-mensajes.component.css'
+})
+export class VerMensajesComponent implements OnInit{
+
+  discucion: Discuciones  | undefined;
+  perfiles:Perfil[] = [];
+  respuestas: Respuestas[] = []
+
+  constructor(
+    private route: ActivatedRoute,
+    private perfilService:PerfilService,
+    private discucionesService: DiscucionesService,
+    private respuestasService:RespuestasService, 
+  ) {}
+
+  ngOnInit() {
+    this.route.params.subscribe(params => {
+      this.perfilService.listar().subscribe(
+        (response)=>{
+          this.perfiles = response;
+        }
+      )
+
+      const id: number = +params['id'];
+      this.discucionesService.buscar(id).subscribe(
+        (data: Discuciones) => {
+          this.discucion = data;
+          this.respuestasService.listar().pipe(
+            map((response: Respuestas[]) => response.filter(respuesta => respuesta.obj_Discuciones.discusionesId === this.discucion?.foroId))
+          ).subscribe(filteredDiscuciones => {
+            this.discuciones = filteredDiscuciones;
+          });
+        }
+      );
+    });
+  }
+}

